mapper.select:
SELECT user_id, session_key, contact_information, nickname, default_shipping_address, department, grade, authority, open_id
FROM user
WHERE nickname = #{nickname}

SELECT user_id, session_key, contact_information, nickname, default_shipping_address, department, grade, authority, open_id
FROM user
WHERE user_id = #{userId}

SELECT user_id
FROM user
WHERE open_id = #{openId}

mapper.update:
UPDATE user

	grade = #{grade},
	contact_information = #{priority},
	default_shipping_address = #{default_shipping_address}

where area_id=#{areaId}
