mapper.select:
SELECT user_id, session_key, contact_information, nickname, default_shipping_address, grade, authority, open_id
FROM user
WHERE nickname = #{nickname}

SELECT user_id, session_key, contact_information, nickname, default_shipping_address, grade, authority, open_id
FROM user
WHERE user_id = #{userId}

SELECT user_id
FROM user
WHERE open_id = #{openId}

mapper.update:
UPDATE user

	grade = #{grade},
	contact_information = #{contactInformation},
	default_shipping_address = #{defaultShippingAddress}

where user_id = #{userId}
